
public  class While_Loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int counter=10;
        while(counter>=1){
        	System.out.println("Current Counter: " +counter + ";  Previous Counter is: " + (counter-1));
        	counter--;
        }
	}

	public void anothermethod(){
		System.out.println("Santanu Ghosh");
	}
	
}
